mapper.select:
select e.*, d.id did, d.name dname, n.id nid, n.name nname, pol.id polid, pol.name polname, j.id jid, j.name jname, pos.id posid, pos.name posname from employee e left join department d on e.departmentId = d.id left join nation n on e.nationId = n.id left join politicsstatus pol on e.politicId = pol.id left join joblevel j on e.jobLevelId = j.id left join position pos on e.posId = pos.id
where e.name like concat('%',#{name},'%')
limit #{page} , #{size}
